@charset "UTF-8";
body {
  margin: 0;
  box-sizing: border-box;
  overflow: hidden;
}

* {
  box-sizing: border-box;
}

#app {
  height: auto;
  width: 100%;
  max-width: 80%;
  margin: 0 auto;
  justify-content: center;
  padding: 0;
  box-sizing: border-box;
  font-family: system-ui;
  background: transparent;
}

.swiper {
  height: 100%;
}

.swiper-wrapper {
  height: 100%;
}

.swiper-slide {
  height: 100%;
}

.swiper-slide img {
  width: 100%;
  height: auto;
  object-fit: contain;
}

.swiper {
  --swiper-theme-color: #FFFFFF; /* 设置Swiper风格 */
  --swiper-navigation-color: #FFFFFF; /* 单独设置按钮颜色 */
  --swiper-navigation-size: 30px; /* 设置按钮大小 */
}

