appsmith.onReady(() => {
  /*
   * This handler function will get called when parent application is ready.
   * Initialize your component here
   * more info - https://docs.appsmith.com/reference/widgets/custom#onready
   */
  new Vue({
    el: "#app",
    data() {
      return {
        currentIndex: 0,
        images: appsmith.model.images
      };
    },
    mounted() {
      var mySwiper = new Swiper('.swiper', {
        autoplay: true,
        //可选选项，自动滑动
        grabCursor: true,
        pagination: {
          el: '.swiper-pagination'
        },
        navigation: {
          nextEl: '.swiper-button-next',
          prevEl: '.swiper-button-prev'
        }
      });
    },
    methods: {
      openPage(data) {
        if (data?.clickUrl) {
          appsmith.updateModel({
            clickUrl: data?.clickUrl
          });
          appsmith.triggerEvent("onOpenPage");
        }
      }
    }
  });
});

