srcDoc.css:
body {
  margin: 0;
  box-sizing: border-box;
  overflow: hidden;
}

* {
  box-sizing: border-box;
}

#app {
  height: auto;
  width: 100%;
  justify-content: center;
  padding: 0;
  box-sizing: border-box;
  font-family: system-ui;
  background: transparent;
}

.list-content {
  width: 100%;
  height: auto;
  background-color: rgb(248, 250, 252);
  padding: 30px 0;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: flex-start;
}

.list-cell {
  width: 100%;
  max-width: 80%;
  height: auto;
  padding: 20px 30px;
  display: flex;
  align-items: center;
  justify-content: center;
  background-color: #FFFFFF;
  border-radius: 25px;
  box-shadow: rgba(0, 0, 0, 0.1) 0 1px 3px 0, rgba(0, 0, 0, 0.06) 0 1px 2px 0 !important;
}

.list-cell + .list-cell {
  margin-top: 30px;
}

.list-cell-left {
  width: calc((100% - 30px) / 2);
  height: auto;
}

.list-cell-left img {
  width: 100%;
  border-radius: 25px;
}

.list-cell-right {
  width: calc((100% - 30px) / 2);
  height: auto;
  margin-left: 30px;
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  justify-content: center;
}

.time-text {
  font-size: 20px;
  color: #0066FF;
  margin-bottom: 10px;
  text-align: left;
}

.title-text {
  font-size: 24px;
  color: #333333;
  margin-bottom: 24px;
  text-align: left;
}

.desc-content {
  width: 100%;
  height: auto;
  margin-bottom: 30px;
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  justify-content: flex-start;
}

.desc-cell {
  font-size: 14px;
  color: #313131;
  text-align: left;
}

.view-btn {
  width: 100%;
  height: 40px;
  border-radius: 25px;
  background-color: rgb(74, 144, 226);
  font-size: 14px;
  color: #FFFFFF;
  line-height: 40px;
  text-align: center;
  cursor: pointer;
}

